mapper.select:
select *
from
(
    select A.*, rownum as RNUM
    from 
    (
		select o.*, m.PRODUCT_NAME, p.PACK_NAME, m.PRODUCT_NO
		from orders o join package p
		on o.PACK_NO = p.PACK_NO
		join menu_info m
		on m.PRODUCT_NO = p.PRODUCT_NO
		
			
				o.user_No=#{userNo}
			
			and o.order_date>=#{startDay}
			
			and o.order_date<to_date(#{endDay})+1
			
		 
		order by o.order_no desc
    ) A
)
where RNUM>#{firstRecordIndex}
 and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

select count(*)
from orders

	
		user_No=#{userNo}
	
	and order_date>=#{startDay}
	
	and order_date<to_date(#{endDay})+1

select * from ( select A.*, rownum as RNUM from ( select m.EXPERT_NO, e.USER_NO, m.PRODUCT_NAME, m.PRODUCT_NO, p.PACK_NAME, u.USER_ID, u.USER_NAME, u.HP, o.ORDER_NO, o.ORDER_ID, o.ORDER_DATE, o.PRICE, o.CONFIRM_DATE, o.PROGRESS, o.FINISH_DATE, o.PACK_NO, o.USER_NO as buy_user_no from orders o join package p on o.PACK_NO = p.PACK_NO join menu_info m on m.PRODUCT_NO = p.PRODUCT_NO join expert e on e.EXPERT_NO = m.EXPERT_NO join user_info u on o.USER_NO = u.USER_NO
e.user_No=#{userNo}
and o.order_date>=#{startDay}
and o.order_date<to_date(#{endDay})+1
order by o.order_no desc ) A ) where RNUM>#{firstRecordIndex} and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

select count(*) from orders o join package p on o.PACK_NO = p.PACK_NO join menu_info m on m.PRODUCT_NO = p.PRODUCT_NO join expert e on e.EXPERT_NO = m.EXPERT_NO
e.USER_NO=#{userNo}
and o.order_date>=#{startDay}
and o.order_date<to_date(#{endDay})+1

mapper.update:
update orders
set progress=#{progress},confirm_date=sysdate
where order_no=#{orderNo}
