mapper.select:
select * from ( select A.*, rownum as RNUM from ( select o.ORDER_NO, o.ORDER_DATE, o.USER_NO, m.PRODUCT_NAME, p.PACK_NAME, pa.PAY_NO, pa.PAY_METHOD, pa.PRICE, pa.REGDATE from menu_info m join package p on m.PRODUCT_NO = p.PRODUCT_NO join orders o on o.PACK_NO = p.PACK_NO join pay pa on pa.ORDER_NO = o.ORDER_NO
o.user_No=#{userNo}
and o.progress = 'F' and pa.regdate>=#{startDay}
and pa.regdate<to_date(#{endDay})+1
order by pa.pay_no desc ) A ) where RNUM>#{firstRecordIndex} and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

select count(*)
from pay p join orders o
on p.ORDER_NO = o.ORDER_NO

	
		user_No=#{userNo}
	
	and regdate>=#{startDay}
	
	and regdate<to_date(#{endDay})+1
